mapper.select:
SELECT COUNT(ask_title) FROM ask_tbl
 where s_id = #{arg0} 
	 where u_id = #{arg1}

SELECT ask_tbl.u_id, ask_tbl.s_id, ask_tbl.p_no, ask_tbl.ask_title, prod_tbl.img, ask_tbl.ask_date from ask_tbl JOIN prod_tbl ON ask_tbl.p_no = prod_tbl.p_no
	 where ask_tbl.s_id = #{arg1} 
	 where ask_tbl.u_id = #{arg2} 
	 ORDER BY ask_tbl.ask_date DESC LIMIT #{arg0.startNum}, #{arg0.amount}
